import React, { useContext, useRef } from 'react'
import { Context } from '../../../../context'
import './listItem.scss'

const ListItem = ({ children, onClick }) => {
  const { darkMode, tab } = useContext(Context)
  const ref = useRef(null)

  const styles = { fontSize: 'calc(12px + 5 * (100vw / 1920))' }

  if (ref.current !== null) {
    if (tab === ref.current.outerText) {
      styles.transform = 'scale(0.97)'
    }
  }

  if (children.length > 16) styles.fontSize = 'calc(8px + 4 * (100vw / 1920))'

  return (
    <button
      style={styles}
      ref={ref}
      type='button'
      className={darkMode ? 'listItem darkMode' : 'listItem'}
      onClick={onClick}>
      {children}
    </button>
  )
}

export default ListItem
