import React, { useState } from 'react'
import './time.scss'
import MyTitle from '../../MyTitle/MyTitle'

const Time = ({ darkMode }) => {
  const [time, setTime] = useState(new Date())

  let localTime = new Date()
  const localeDate = new Date().toLocaleDateString()

  setInterval(() => {
    localTime = new Date()
  }, 100)

  console.log(localTime)
  return (
    <div className={darkMode ? 'mainContent__time darkMode' : 'mainContent__time'}>
      <MyTitle fontSize='18px' lineHeight='25px' letterSpacing='0.02em'>
        Wel wel wel!
      </MyTitle>
      <div className='time__container'>
        <div className='time__block'>
          <span className='time__helpTitle'>Now</span>
          <div>{localTime}</div>
        </div>
        <div className='time__block'>
          <span className='time__helpTitle'>Date</span>
        </div>
      </div>
    </div>
  )
}

export default Time
