import { useEffect } from 'react'
import { Context } from '../context'
import { database } from '../firebase/firebaseConfig'
import useValue from '../hooks/useValue'
import useLocaleStorage from '../hooks/useLocaleStorage'
import defaultPhoto from '../assets/img/default-profile-picture.jpg'

const withApp = Component => {
  return () => {
    // dark mode
    const [darkMode, setDarkMode] = useLocaleStorage('darkMode', false)
    // side menu viewport < 1280
    const [sideMenu, openSideMenu] = useValue(false)
    // modal windows
    const [modals, openModals] = useValue({
      authModal: false,
      taskModal: false,
      calendarModal: false,
      statisticsModal: false,
      deleteCategoryModal: false,
    })
    const [authInfoModal, createAuthInfoModal] = useValue({
      show: false,
      type: '',
      text: '',
    })
    // date
    const [calendarDate, setCalendarDate] = useValue(new Date())
    const [timeLine, setTimeLine] = useValue({ past: false, future: false })
    // user information
    const [userInfo, setUserInfo] = useLocaleStorage('userInfo', {
      photo: defaultPhoto,
      nick: 'username',
      email: '',
      uid: '',
    })
    // data
    const [tabsStorage, setDataInStorage] = useLocaleStorage('data', {
      date: new Date().toLocaleDateString().split('.').reverse().join(''),
      tasks: [],
    })
    const [tabItems, setTabItems] = useValue(tabsStorage)
    // select tabs and category
    const [tab, setTab] = useValue(tabItems.tasks.length && tabItems.tasks[0].title)
    const [category, setCategory, categorySelectOnChange] = useValue(
      tabItems.tasks.length && tabItems.tasks[0].title,
    )

    useEffect(() => {
      const date = new Date().toLocaleDateString().split('.').reverse().join('')

      if (userInfo.uid && date !== tabItems.date) {
        database.writeNewDayData(
          userInfo.uid,
          calendarDate.toLocaleDateString().replaceAll('.', ''),
          setTabItems,
          setCategory,
          setTab,
        )
      }
    }, [])

    useEffect(() => {
      timeLine.past === timeLine.future && setDataInStorage(tabItems)
    }, [tabItems])

    // select calendar value
    const selectData = date => {
      const selectedDate = +date.toLocaleDateString().split('.').reverse().join('')
      const present = +new Date().toLocaleDateString().split('.').reverse().join('')

      if (selectedDate < present) {
        setTimeLine({ past: selectedDate < present, future: selectedDate > present })
        return database.readPastData({
          userInfo,
          date,
          tabItems,
          setTabItems,
          setCategory,
          setTab,
          modals,
          openModals,
          setCalendarDate,
          createAuthInfoModal,
        })
      } else if (selectedDate > present) {
        setTimeLine({ past: selectedDate < present, future: selectedDate > present })
        setCalendarDate(date)
        openModals({ ...modals, calendarModal: false })
        return database.writeNewDayData(
          userInfo.uid,
          date.toLocaleDateString().replaceAll('.', ''),
          setTabItems,
          setCategory,
          setTab,
        )
      }

      setTimeLine({ past: false, future: false })
      setCalendarDate(date)
      setTabItems(tabsStorage)
      setCategory(tabsStorage.tasks.length && tabsStorage.tasks[0].title)
      setTab(tabsStorage.tasks.length && tabsStorage.tasks[0].title)
      openModals({ ...modals, calendarModal: false })
    }

    const tasksMethods = {
      createTask: (inputValue, setInputValue) => {
        setInputValue('')
        openModals({ ...modals, taskModal: false })
        setTab(category)

        tabItems.tasks.forEach(tab => {
          if (tab.title === category) {
            tab.data = [
              {
                task: inputValue,
                time: new Date().toLocaleTimeString(),
                completed: false,
              },
              ...tab.data,
            ]
          }
        })

        database.writeUserTasksData(
          userInfo.uid,
          calendarDate.toLocaleDateString().replaceAll('.', ''),
          tabItems,
        )

        return setTabItems({ ...tabItems })
      },
      deleteTask: (title, currentTask) => {
        tabItems.tasks.forEach(tab => {
          if (title === tab.title) {
            tab.data = tab.data.filter(task => task.time !== currentTask.time)
          }
        })

        database.writeUserTasksData(
          userInfo.uid,
          calendarDate.toLocaleDateString().replaceAll('.', ''),
          tabItems,
        )

        return setTabItems({ ...tabItems })
      },
      checkTask: (title, currentTask, complete) => {
        tabItems.tasks.forEach(tab => {
          if (title === tab.title) {
            tab.data.forEach(task => {
              if (currentTask.time === task.time) task.completed = !complete
            })
          }
        })

        database.writeUserTasksData(
          userInfo.uid,
          calendarDate.toLocaleDateString().replaceAll('.', ''),
          tabItems,
        )

        return setTabItems({ ...tabItems })
      },
      editTask: (event, title, currentTask, newValue) => {
        event.target.style.borderBottom = `1px solid ${event.target.value ? 'transparent' : 'red'}`
        event.target.readOnly = event.code === 'Enter' && event.target.value

        tabItems.tasks.forEach(tab => {
          if (title === tab.title) {
            tab.data.forEach(task => {
              if (currentTask.task === task.task) task.task = newValue
            })
          }
        })

        database.writeUserTasksData(
          userInfo.uid,
          calendarDate.toLocaleDateString().replaceAll('.', ''),
          tabItems,
        )

        return setTabItems({ ...tabItems })
      },
      setSortType: value => {
        tabItems.tasks.forEach(category => {
          if (category.title === tab) {
            category.sortingType = value

            category.data.sort((a, b) => {
              const first = a.time.replaceAll(':', '')
              const second = b.time.replaceAll(':', '')

              return category.sortingType === 'newest first' ? second - first : first - second
            })
          }
        })

        database.writeUserTasksData(
          userInfo.uid,
          calendarDate.toLocaleDateString().replaceAll('.', ''),
          tabItems,
        )

        return setTabItems({ ...tabItems })
      },
    }

    // global context
    const contextValues = {
      darkMode,
      setDarkMode,
      modals,
      openModals,
      authInfoModal,
      createAuthInfoModal,
      sideMenu,
      openSideMenu,
      calendarDate,
      setCalendarDate,
      timeLine,
      setTimeLine,
      selectData,
      setDataInStorage,
      userInfo,
      setUserInfo,
      tab,
      setTab,
      tabItems,
      setTabItems,
      defaultPhoto,
      category,
      setCategory,
      categorySelectOnChange,
      tasksMethods,
    }

    return (
      <Context.Provider value={contextValues}>
        <Component darkMode={darkMode} />
      </Context.Provider>
    )
  }
}

export { withApp }

///////////////////////////////////////////////////////

import { useState } from 'react'
import './styles.css'

const API_URL = 'https://api.github.com'

const fetchResults = async query => {
  try {
    const response = await (await fetch(`${API_URL}/search/users?q=${query}`)).json()
    return response.items || []
  } catch (e) {
    throw new Error(e)
  }
}

const App = () => {
  const [query, setQuery] = useState('')
  const [results, setResults] = useState([])

  function onSearchChange(event) {
    setQuery(event.target.value)
  }

  async function onSearchSubmit(event) {
    event.preventDefault()
    const results = await fetchResults(query)
    setResults(results)
  }

  return (
    <div className='app'>
      <main className='main'>
        <h2>Project 5: GitHub User Search</h2>
        <Form onChange={onSearchChange} onSubmit={onSearchSubmit} value={query} />
        <h3>Results</h3>
        <div id='results'>
          <div>
            {results.map(user => (
              <User
                key={user.login}
                avatar={user.avatar_url}
                url={user.html_url}
                username={user.login}
              />
            ))}
          </div>
        </div>
      </main>
    </div>
  )
}

const User = ({ avatar, url, username }) => {
  return (
    <div className='user'>
      <img src={avatar} alt='Profile' width='50' height='50' />
      <a href={url} target='_blank' rel='noopener noreferrer'>
        {username}
      </a>
    </div>
  )
}

const Form = ({ onSubmit, onChange, value }) => {
  return (
    <form className='search-form' onSubmit={onSubmit}>
      <input
        id='search'
        type='text'
        placeholder='Enter username or email'
        onChange={onChange}
        value={value}
      />
      <button type='submit'>Search</button>
    </form>
  )
}

export default App
