import { useContext } from 'react'
import { Context } from '../../../../../context'
import { database } from '../../../../../firebase/firebaseConfig'
import useValue from '../../../../../hooks/useValue'
import CategoryHeader from './CategoryHeader'
import TasksContainer from './TasksContainer'
import CategoriesConfig from '../CategoriesConfig/CategoriesConfig'
import DeleteModal from '../../../modalWindows/DeleteModal/DeleteModal'
import './categoriesTab.scss'

const CategoriesTab = props => {
  const [modal, setModal] = useValue(false)

  const {
    darkMode,
    tab,
    setTab,
    setCategory,
    tabItems,
    setTabItems,
    timeLine,
    userInfo,
    calendarDate,
    modals,
    openModals,
    tasksMethods,
  } = useContext(Context)

  const removeCategory = () => {
    const newTabItems = {
      ...tabItems,
      tasks: tabItems.tasks.filter(tab => tab.title !== props.category.title),
    }

    // Find the index of the previous tab, or the second tab if the current tab is the first one
    const newTabIndex = 0 || tabItems.tasks.findIndex(tab => tab.title === props.category.title) - 1

    const findTitle = () => {
      return tabItems.tasks.length
        ? tabItems.tasks[tabItems.tasks.findIndex(tab => (tab.title === props.category.title) - 1)]
            .title
        : 'f'
    }

    setTabItems(newTabItems)
    setTab(findTitle)
    setCategory(findTitle)

    return database.writeUserTasksData(
      userInfo.uid,
      calendarDate.toLocaleDateString().split('.').join(''),
      newTabItems,
    )
  }

  const handleDeleteCategory = () => openModals({ ...modals, deleteCategoryModal: true })

  const propsObjForTasks = {
    darkMode,
    tabTitle: props.category.title,
    tasksData: props.category.data,
    tasksMethods,
    timeLine,
  }

  return (
    <div
      className={
        tab === props.category.title ? 'category__container active' : 'category__container'
      }>
      <CategoryHeader categoryTitle={props.category.title} modal={modal} setModal={setModal} />

      <TasksContainer type='Active' {...propsObjForTasks} />
      <TasksContainer type='Completed' {...propsObjForTasks} />

      <CategoriesConfig
        modal={modal}
        timeLine={timeLine}
        category={props.category}
        setSortType={tasksMethods.setSortType}
        handleDeleteCategory={handleDeleteCategory}
        darkMode={darkMode}
      />

      <DeleteModal removeCategory={removeCategory} />
    </div>
  )
}

export default CategoriesTab
