import React, { useState } from 'react'
import './App.scss'
import AsideBar from '../AsideBar/AsideBar'
import MainContent from '../MainContent/MainContent'
import TaskModal from '../TaskModal/TaskModal'

const defaultListItems = [
  {
    title: 'Home',
    data: [
      { task: 'Learn Git', date: new Date(), completed: true },
      { task: 'Learn Html', date: new Date(), completed: true },
      { task: 'Learn Css', date: new Date(), completed: true },
      { task: 'Learn Next.js', date: new Date(), completed: false },
      { task: 'Learn Js', date: new Date(), completed: true },
      { task: 'Learn React.js', date: new Date(), completed: false },
    ],
  },
  {
    title: 'Work',
    data: [
      { task: 'Learn Html', date: new Date(), completed: true },
      { task: 'Learn Js', date: new Date(), completed: true },
      { task: 'Learn React', date: new Date(), completed: false },
    ],
  },
  {
    title: 'Sport',
    data: [
      { task: 'Learn Html,Css', date: new Date(), completed: true },
      { task: 'Learn React', date: new Date(), completed: false },
      { task: 'Learn Js', date: new Date(), completed: true },
    ],
  },
]

const App = () => {
  const [darkMode, setDarkMode] = useState(false)
  const [listItems, setListItem] = useState(defaultListItems)
  const [tab, setTab] = useState(defaultListItems[0].title)
  const [taskModal, setTaskModal] = useState(false)

  const createTask = (task, setTask) => {
    setTask({ input: '', select: '' })
    setTaskModal(false)

    return setListItem(
      listItems.forEach(e => {
        if (e.title === task.select) {
          e.data.push({ task: task.input, date: new Date(), completed: false })
        }
      }),
    )
  }

  return (
    <div className={darkMode ? 'App darkMode' : 'App'}>
      <div className='app__container'>
        <AsideBar
          darkMode={darkMode}
          listItems={listItems}
          setListItem={setListItem}
          tab={tab}
          setTab={setTab}
        />
        <MainContent
          darkMode={darkMode}
          setDarkMode={setDarkMode}
          setTaskModal={setTaskModal}
          listItems={listItems}
          setListItem={setListItem}
          tab={tab}
          setTab={setTab}
        />
        <TaskModal
          darkMode={darkMode}
          taskModal={taskModal}
          setTaskModal={setTaskModal}
          listItems={listItems}
          createTask={createTask}
        />
      </div>
    </div>
  )
}

export default App
