import React, { useEffect } from 'react'
import CategoriesTab from './CategoriesTab/CategoriesTab'
import './tasks.scss'

const Tasks = props => {
  const classes = ['mainContent__tabsContainer']

  if (props.darkMode) classes.push('darkMode')

  if (props.listItems.length) {
    return (
      <div className={classes.join(' ')}>
        {useEffect(() => {
          props.listItems.map((category, idx) => (
            <CategoriesTab
              darkMode={props.darkMode}
              key={category.title}
              category={category}
              idx={idx}
              tab={props.tab}
              setTab={props.setTab}
              listItems={props.listItems}
              setListItem={props.setListItem}
              deleteTask={props.deleteTask}
            />
          ))
        })}
      </div>
    )
  }

  return <div className='noCategory'>create a category and add tasks</div>
}

export default Tasks
