import React from 'react'
import { Transition } from 'react-transition-group'
import './loader.scss'

const Loader = loader => {
  return (
    <Transition in={loader} timeout={500}>
      {state => (
        <div className={`loader ${state}`}>
          <div className='loader__circle'></div>
        </div>
      )}
    </Transition>
  )
}

export default Loader
