import React from 'react'
import CategoryTask from './CategoryTask'
import './tasks.scss'

const Tasks = ({ darkMode, listItems, setListItems }) => {
  return (
    <div
      className={darkMode ? 'mainContent__tasksContainer darkMode' : 'mainContent__tasksContainer'}>
      {listItems.map(category => (
        <CategoryTask key={category} category={category} />
      ))}
    </div>
  )
}

export default Tasks
